+/* Базовые стили для страницы */
+body {
+  font-family: Arial, sans-serif;
+  background: #f4f4f4;
+  display: flex;
+  flex-direction: column;
+  align-items: center;
+  padding: 20px;
+}
+
+h1 {
+  margin-bottom: 20px;
+}
+
+/* Контейнер всех календарей горизонтально */
+#calendars-container {
+  display: flex;
+  flex-wrap: wrap;
+  gap: 20px;
+  justify-content: center;
+}
+
+/* Каждый календарь */
+.calendar-container {
+  flex: 0 0 300px;
+  border: 1px solid #ccc;
+  padding: 10px;
+  background: #fff;
+  border-radius: 8px;
+  box-shadow: 2px 2px 6px rgba(0,0,0,0.1);
+}
+
+.calendar-title {
+  text-align: center;
+  margin-bottom: 10px;
+  font-weight: bold;
+}
+
+/* Сетка дней */
+.calendar {
+  display: grid;
+  grid-template-columns: repeat(7, 35px);
+  gap: 4px;
+}
+
+.day {
+  width: 35px;
+  height: 35px;
+  display: flex;
+  flex-direction: column;
+  align-items: center;
+  justify-content: center;
+  background: white;
+  border: 1px solid #ccc;
+  font-size: 12px;
+  cursor: pointer;
+  position: relative;
+  border-radius: 4px;
+  padding: 2px;
+}
+
+/* Блок привычки внутри дня */
+.habit {
+  font-size: 16px;
+  cursor: pointer;
+  user-select: none;
+}
+
+/* Успех — зелёная галочка */
+.habit.success {
+  color: rgba(0, 200, 0, 0.6); /* зелёный полупрозрачный */
+}
+
+.habit.success::before {
+  content: "✔";
+}
+
+/* Пропуск / неудача — красный крестик */
+.habit.fail {
+  color: rgba(255, 0, 0, 0.8);
+}
+
+.habit.fail::before {
+  content: "✖";
+}

